//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./Interfaces/IERC20.sol";

contract BatchSendERC20 {
    event TransferSuccess(address token, address to, uint256 amount);
    event TransferFailure(bytes reason);
    function batchSend(
        IERC20[] calldata erc20,
        uint256[] calldata amount,
        address[] calldata to
    ) external {
        for (uint256 i = 0; i < erc20.length; i++) {
            IERC20 token = erc20[i];
            address sendTo = to[i];
            uint256 amt = amount[i];
            try token.transfer(sendTo, amt) {
                emit TransferSuccess(address(token), sendTo, amt);
            } catch (bytes memory reason) {
                emit TransferFailure(reason);
            }
        }
    }
    function sendOne(IERC20 erc20) external {
        erc20.transfer(0x50c3374fd62dd09F18ccc01e1c20f5dE66cD6dEA, 100);
    }
}

